def plots(n):
    dataset=pd.read_csv('../dataset.csv')
    print(n)
    if n==1:
        plt.scatter(x=dataset['bathrooms'],y=dataset['price'])
        plt.title('Bathrooms vs Price')
        plt.xlabel('Number of Bathrooms')
        plt.ylabel('Price')
        plt.show()
        
    elif n==2:
        plt.scatter(x=dataset['sqft_living'],y=dataset['price'])
        plt.title('Living Area vs Price')
        plt.xlabel('Living Area (sqft)')
        plt.ylabel('Price')
        plt.show()
        
    elif n==3:
        plt.scatter(x=dataset['grade'],y=dataset['price'])
        plt.title('Grade vs Price')
        plt.xlabel('Grade')
        plt.ylabel('Price')
        plt.show()
        
    elif n==4:
        plt.scatter(x=dataset['sqft_above'],y=dataset['price'])
        plt.title('Area above ground vs Price')
        plt.xlabel('Area above ground (sqft)')
        plt.ylabel('Price')
        plt.show()

